+<?php
+namespace App\Http\Controllers;
+
+use App\Models\Education;
+use Illuminate\Http\Request;
+
+class EducationController extends Controller
+{
+    public function index()
+    {
+        $educations = Education::with('personalInfo')->get();
+        return response()->json($educations);
+    }
+
+    public function store(Request $request)
+    {
+        $validated = $request->validate([
+            'personal_info_id' => 'required|exists:personal_infos,id',
+            'degree' => 'required|string|max:255',
+            'school_name' => 'required|string|max:255',
+            'field_of_study' => 'nullable|string|max:255',
+            'start_date' => 'required|date',
+            'graduation_date' => 'nullable|date|after_or_equal:start_date',
+        ]);
+
+        $education = Education::create($validated);
+
+        return response()->json(['message' => 'Education created successfully', 'data' => $education]);
+    }
+
+    public function show(Education $education)
+    {
+        $education->load('personalInfo');
+        return response()->json($education);
+    }
+
+    public function update(Request $request, Education $education)
+    {
+        $validated = $request->validate([
+            'degree' => 'sometimes|required|string|max:255',
+            'school_name' => 'sometimes|required|string|max:255',
+            'field_of_study' => 'nullable|string|max:255',
+            'start_date' => 'sometimes|required|date',
+            'graduation_date' => 'nullable|date|after_or_equal:start_date',
+        ]);
+
+        $education->update($validated);
+
+        return response()->json(['message' => 'Education updated successfully', 'data' => $education]);
+    }
+
+    public function destroy(Education $education)
+    {
+        $education->delete();
+
+        return response()->json(['message' => 'Education deleted successfully']);
+    }
+}
